mapper.update:
UPDATE


    AND userName = #{userName, jdbcType=VARCHAR}


    AND userPhone = #{userPhone, jdbcType=VARCHAR}

SET userType = #{userType}, updateTime = CURRENT_TIMESTAMP
WHERE userId = #{userId}

mapper.select:
SELECT

FROM

where 1=1

    AND userPhone = #{userPhone, jdbcType=VARCHAR}


    AND userName = #{userPhone, jdbcType=VARCHAR}


    AND appId = #{userPhone, jdbcType=VARCHAR}


    AND userType = #{userPhone, jdbcType=VARCHAR}

ORDER BY updateTime DESC
