mapper.select:
SELECT

FROM

WHERE 1=1

    AND housesId = #{housesId, jdbcType=VARCHAR}


    AND userId = #{userId, jdbcType=VARCHAR}

ORDER BY updateTime DESC

SELECT
housesId
FROM

WHERE 1=1

    AND housesName = #{housesId, jdbcType=VARCHAR}


    AND developers = #{userId, jdbcType=VARCHAR}

SELECT
FROM house_type WHERE 1=1
AND houseTypeId = #{houseTypeId}

mapper.update:
UPDATE houses_spclty
SET housesSpclty = #{housesSpclty, jdbcType=VARCHAR},
SET updateTime = CURRENT_TIMESTAMP WHERE housesSpcltyId = #{housesSpcltyId}

UPDATE houses_picture
SET housesPicture = #{housesPicture, jdbcType=VARCHAR},
SET updateTime = CURRENT_TIMESTAMP WHERE housesPictureId = #{housesPictureId}

UPDATE houses_facilities
SET housesFacilities = #{housesFacilities, jdbcType=VARCHAR},
SET updateTime = CURRENT_TIMESTAMP WHERE housesFacilitiesId = #{housesFacilitiesId}
